import React, { useEffect, useState } from 'react';
import { StyleSheet, Text, View } from 'react-native';
import * as firebase from 'firebase';
import 'firebase/firestore';
import { FirebaseDataType } from './components/types/type';

if (!firebase.apps.length) {
  // For Firebase JS SDK v7.20.0 and later, measurementId is optional
  var firebaseConfig = {
    apiKey: 'AIzaSyD013tqcRp9SDxsXTb5TteohUpdMs6_Ims',
    authDomain: 'shop-review-1047a.firebaseapp.com',
    databaseURL: 'https://shop-review-1047a.firebaseio.com',
    projectId: 'shop-review-1047a',
    storageBucket: 'shop-review-1047a.appspot.com',
    messagingSenderId: '894475344445',
    appId: '1:894475344445:web:41c81484dcba868e936854',
    measurementId: 'G-PEWWQ2JZNS',
  };
  // Initialize Firebase
  firebase.initializeApp(firebaseConfig);
}

const App: React.FC = () => {
  const [shopData, setShopData] = useState<FirebaseDataType>([]);
  useEffect(() => {
    getFirebaseItems();
  }, []);

  const getFirebaseItems = async () => {
    const snapshot = await firebase.firestore().collection('shops').get();
    const shops = snapshot.docs.map((shop) => shop.data());
    console.log(shops);
  };

  return (
    <View style={styles.container}>
      <Text>Yusuke's App</Text>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
});

export default App;
